import React, { useState } from 'react';
import api from '../api';

export default function ForgotPassword({ onClose, onSuccess }) {
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  async function handleSubmit(e) {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const { data } = await api.post('/auth/check-email', { email });

      if (data.exists) {
        // Chama callback para abrir a tela de nova senha
        onSuccess(email);
      } else {
        setError('E-mail não encontrado.');
      }
    } catch {
      setError('Erro ao verificar e-mail.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="modal-backdrop show" style={{ display: 'block' }}>
      <div className="modal" style={{ display: 'block' }}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Recuperar senha</h5>
              <button type="button" className="btn-close" onClick={onClose}></button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleSubmit}>
                <input
                  type="email"
                  className="form-control mb-2"
                  placeholder="Digite seu e-mail"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                {error && <div className="alert alert-danger py-2">{error}</div>}
                <button disabled={loading} className="btn btn-primary w-100">
                  {loading ? 'Verificando...' : 'Recuperar senha'}
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
