import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api, { setToken } from '../api';

export default function Login(){
  const nav = useNavigate();
  const [email,setEmail] = useState('');
  const [senha,setSenha] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function submit(e){
    e.preventDefault();
    setError('');
    setLoading(true);
    try{
      const { data } = await api.post('/auth/login', { email, password: senha });
      setToken(data.access_token);
      nav('/app');
    }catch(err){
      setError(err?.response?.data?.msg || 'Falha no login');
    }finally{
      setLoading(false);
    }
  }

  return (
    <div className="container-fluid">
      {/* ocupa a altura da janela */}
      <div className="row min-vh-100">
        {/* Coluna esquerda: logo / branding */}
        <div className="col-12 col-lg-6 d-flex align-items-center justify-content-center" style={{background: 'var(--brand-bg)', color: 'var(--brand-text)'}}>
          <div className="text-center p-4">
            {/* Troque /logo.png pelo seu arquivo (coloque em public/logo.png) */}
            {/* Altura controlada para manter proporção */}
            <img src="/logo.png" alt="Logo AJ7" style={{maxWidth: '75%', height: 'auto', maxHeight: 220}} />
            <h2 className="mt-4 fw-semibold">Portal de Comissões</h2>
            <p className="mt-2 mb-0" style={{opacity: .85}}>AJ7 Seguros — Gestão de Vendas e Comissões</p>
          </div>
        </div>

        {/* Coluna direita: formulário */}
        <div className="col-12 col-lg-6 d-flex align-items-center justify-content-center">
          <div className="w-100" style={{maxWidth: 420, padding: '2rem'}}>
            <h3 className="mb-3 text-center">Entrar</h3>
            <form onSubmit={submit}>
              <div className="mb-3">
                <label className="form-label">E-mail</label>
                <input
                  className="form-control"
                  placeholder="ex: usuario@empresa.com"
                  value={email}
                  onChange={e=>setEmail(e.target.value)}
                  type="email"
                  required
                  autoFocus
                />
              </div>

              <div className="mb-2">
                <label className="form-label">Senha</label>
                <input
                  className="form-control"
                  placeholder="Sua senha"
                  type="password"
                  value={senha}
                  onChange={e=>setSenha(e.target.value)}
                  required
                />
              </div>

              {error && <div className="alert alert-danger py-2 mt-2">{error}</div>}

              <button disabled={loading} className="btn btn-primary w-100 mt-3">
                {loading? 'Entrando...' : 'Entrar'}
              </button>
            </form>

            <div className="text-center mt-4">
              <small className="text-muted">© Daniel Correa</small>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
