import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import api, { setUser, setToken, getUserInfo } from './api';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Sales from './pages/Sales';
import ManageSellers from './pages/ManageSellers';
import ManageBanks from './pages/ManageBanks';
import ManageStores from './pages/ManageStores';

function App() {
  const [user, setUserState] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setToken(token);
      getUserInfo()
        .then((u) => {
          setUserState(u);
          setUser(u);
        })
        .catch(() => {
          setUserState(null);
          localStorage.removeItem('token');
        })
        .finally(() => setLoading(false));
    } else {
      setLoading(false);
    }
  }, []);

  if (loading) return <div>Carregando...</div>;

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            user ? <Navigate to="/dashboard" replace /> : <Login setUser={(u) => {
              setUserState(u);
              setUser(u);
            }} />
          }
        />
        <Route
          path="/dashboard"
          element={
            user ? <Dashboard user={user} /> : <Navigate to="/" replace />
          }
        />
        <Route
          path="/sales"
          element={
            user ? <Sales user={user} /> : <Navigate to="/" replace />
          }
        />
        <Route
          path="/manage-sellers"
          element={
            user && user.role === 'admin' ? <ManageSellers /> : <Navigate to="/" replace />
          }
        />
        <Route
          path="/manage-banks"
          element={
            user && user.role === 'admin' ? <ManageBanks /> : <Navigate to="/" replace />
          }
        />
        <Route
          path="/manage-stores"
          element={
            user && user.role === 'admin' ? <ManageStores /> : <Navigate to="/" replace />
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
