import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// PÁGINAS (conforme seus arquivos existentes)
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';       // Dashboard principal
import Sales from './pages/SalesPage';           // Tela de Vendas
import AdminCadastros from './pages/AdminCadastros';
import ManageBanks from './pages/ManageBanks';
import ManageStores from './pages/ManageStores';
import ManageSellers from './pages/ManageSellers';

import { setToken, getUserInfo } from './api';

// --- Helpers de proteção de rota ---
function isAuth() {
  return !!localStorage.getItem('token');
}

function Private({ children }) {
  return isAuth() ? children : <Navigate to="/login" replace />;
}

function AdminOnly({ children }) {
  const user = getUserInfo();
  return isAuth() && user?.role === 'admin' ? children : <Navigate to="/login" replace />;
}

export default function App() {
  // Reaplica o token salvo (para axios) ao montar
  React.useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) setToken(token);
  }, []);

  return (
    <Router>
      <Routes>
        {/* Raiz: manda para /app se logado, senão para /login */}
        <Route
          path="/"
          element={isAuth() ? <Navigate to="/app" replace /> : <Navigate to="/login" replace />}
        />

        {/* Login */}
        <Route path="/login" element={<Login />} />

        {/* Dashboard principal — seu Login.jsx faz nav('/app') */}
        <Route
          path="/app"
          element={
            <Private>
              <Dashboard />
            </Private>
          }
        />

        {/* Vendas */}
        <Route
          path="/sales"
          element={
            <Private>
              <Sales />
            </Private>
          }
        />

        {/* Módulo de Cadastros (apenas admin) */}
        <Route
          path="/cadastros"
          element={
            <AdminOnly>
              <AdminCadastros />
            </AdminOnly>
          }
        />
        <Route
          path="/cadastros/bancos"
          element={
            <AdminOnly>
              <ManageBanks />
            </AdminOnly>
          }
        />
        <Route
          path="/cadastros/lojas"
          element={
            <AdminOnly>
              <ManageStores />
            </AdminOnly>
          }
        />
        <Route
          path="/cadastros/vendedores"
          element={
            <AdminOnly>
              <ManageSellers />
            </AdminOnly>
          }
        />

        {/* Fallback */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}
