import React, { useState } from 'react';
import api from '../api';

export default function ResetPassword({ email, onClose, onSuccess }) {
  const [pwd1, setPwd1] = useState('');
  const [pwd2, setPwd2] = useState('');
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState('');
  const [error, setError] = useState('');

  async function handleSubmit(e) {
    e.preventDefault();
    setMsg(''); setError('');

    if (pwd1.length < 6) {
      setError('A nova senha deve ter pelo menos 6 caracteres.');
      return;
    }
    if (pwd1 !== pwd2) {
      setError('As senhas não conferem.');
      return;
    }

    setLoading(true);
    try {
      // Reseta direto via e-mail + nova senha
      const { data } = await api.post('/auth/reset-password', {
        email,
        password: pwd1
      });
      setMsg(data?.msg || 'Senha redefinida com sucesso.');
      // Pequeno delay opcional para UX, depois fecha
      setTimeout(() => {
        onSuccess?.();
      }, 600);
    } catch (err) {
      setError(err?.response?.data?.msg || 'Falha ao redefinir a senha.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div style={backdropStyle}>
      <div style={modalStyle}>
        <div className="d-flex align-items-center mb-3">
          <h5 className="m-0">Definir nova senha</h5>
          <button className="btn btn-sm btn-outline-secondary ms-auto" onClick={onClose}>
            Fechar
          </button>
        </div>

        <div className="mb-2 text-muted small">
          E-mail: <strong>{email}</strong>
        </div>

        <form onSubmit={handleSubmit}>
          <input
            type="password"
            className="form-control mb-2"
            placeholder="Nova senha"
            value={pwd1}
            onChange={e=>setPwd1(e.target.value)}
            required
          />
          <input
            type="password"
            className="form-control mb-2"
            placeholder="Confirmar nova senha"
            value={pwd2}
            onChange={e=>setPwd2(e.target.value)}
            required
          />

          {msg && <div className="alert alert-success py-2">{msg}</div>}
          {error && <div className="alert alert-danger py-2">{error}</div>}

          <button
            type="submit"
            className="btn btn-primary w-100"
            disabled={loading}
          >
            {loading ? 'Salvando...' : 'Salvar nova senha'}
          </button>
        </form>
      </div>
    </div>
  );
}

const backdropStyle = {
  position: 'fixed',
  inset: 0,
  background: 'rgba(0,0,0,0.35)',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  zIndex: 1060
};
const modalStyle = {
  width: '100%',
  maxWidth: 420,
  background: '#fff',
  borderRadius: 12,
  padding: '16px 16px 18px',
  boxShadow: '0 8px 30px rgba(0,0,0,0.25)'
};
