import React from 'react';
import { Link, NavLink, useNavigate, useLocation } from 'react-router-dom';
import { getUserInfo, setToken, setUser } from '../api';

export default function Shell({ onLogout, children }) {
  const nav = useNavigate();
  const loc = useLocation();
  const user = getUserInfo();
  const isAdmin = user?.role === 'admin';

  function logout() {
    setToken(null);
    setUser(null);
    onLogout?.();
    nav('/login', { replace: true });
  }

  // Rotas para marcar a subnav ativa
  const subnav = [
    { to: '/app', label: 'Dashboard', show: true },
    { to: '/sales', label: 'Vendas', show: !isAdmin }, // vendedores
    { to: '/cadastros', label: 'Cadastro', show: isAdmin }, // admin
  ];

  return (
    <div>
      {/* Topo */}
      <header className="app-header border-bottom">
        <div className="container py-3 d-flex align-items-center">
          <Link to="/app" className="d-flex align-items-center text-decoration-none">
            <img src="/logo.png" alt="AJ7" style={{ height: 42, marginRight: 12 }} />
            <div className="app-title">Controle de Vendas e Comissões</div>
          </Link>
          <div className="ms-auto text-end">
            {user && (
              <>
                <div className="small text-muted">Logado como:</div>
                <div className="fw-semibold">{user.nome}</div>
              </>
            )}
            <button onClick={logout} className="btn btn-sm btn-outline-secondary mt-1">
              Sair
            </button>
          </div>
        </div>

        {/* Subnav azul escuro */}
        <div style={{ background: 'var(--brand-bg)' }}>
          <div className="container">
            <nav className="app-subnav">
              {subnav.filter(i => i.show).map(i => (
                <NavLink
                  key={i.to}
                  to={i.to}
                  className={({ isActive }) =>
                    'nav-link ' + (isActive || loc.pathname === i.to ? 'active' : '')
                  }
                >
                  {i.label}
                </NavLink>
              ))}
            </nav>
          </div>
        </div>
      </header>

      {/* Conteúdo */}
      <main className="container py-4">{children}</main>

      {/* Rodapé opcional */}
      <footer className="text-center py-3 text-muted">
        © {new Date().getFullYear()} <strong>Daniel Correa</strong>
      </footer>
    </div>
  );
}
