// frontend/src/pages/ResetPassword.jsx
import React, { useEffect, useMemo, useState } from 'react';
import api from '../api';

// util simples para ler ?token=... da URL
function getQueryParam(name) {
  const params = new URLSearchParams(window.location.search);
  return params.get(name) || '';
}

// força da senha (0 a 4) + rótulo
function scorePassword(pwd) {
  let score = 0;
  if (!pwd) return { score: 0, label: 'Muito fraca' };

  const len = pwd.length;
  const hasLower = /[a-z]/.test(pwd);
  const hasUpper = /[A-Z]/.test(pwd);
  const hasNumber = /[0-9]/.test(pwd);
  const hasSymbol = /[^A-Za-z0-9]/.test(pwd);

  if (len >= 8) score += 1;
  if (len >= 12) score += 1;         // bônus por comprimento
  const variety = [hasLower, hasUpper, hasNumber, hasSymbol].filter(Boolean).length;
  score += Math.max(0, variety - 1); // 0..3 pontos extras por variedade

  // normaliza para 0..4
  score = Math.max(0, Math.min(4, score));

  const labelMap = ['Muito fraca', 'Fraca', 'Ok', 'Forte', 'Muito forte'];
  return { score, label: labelMap[score] };
}

export default function ResetPassword() {
  const [token, setToken] = useState('');
  const [nova, setNova] = useState('');
  const [confirm, setConfirm] = useState('');
  const [showPwd, setShowPwd] = useState(false);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState('');
  const [successMsg, setSuccessMsg] = useState('');

  useEffect(() => {
    setToken(getQueryParam('token'));
  }, []);

  const { score, label } = useMemo(() => scorePassword(nova), [nova]);
  const progressPct = [5, 25, 50, 75, 100][score]; // evita 0% ficar “invisível”
  const progressClass =
    score <= 1 ? 'bg-danger' :
    score === 2 ? 'bg-warning' :
    score === 3 ? 'bg-info' : 'bg-success';

  async function submit(e) {
    e.preventDefault();
    setErr('');
    if (!token) {
      setErr('Token inválido ou ausente. Refaça a recuperação de senha.');
      return;
    }
    if (nova.length < 6) {
      setErr('A senha deve ter pelo menos 6 caracteres.');
      return;
    }
    if (nova !== confirm) {
      setErr('As senhas não coincidem.');
      return;
    }
    setLoading(true);
    try {
      const { data } = await api.post('/auth/reset', { token, nova_senha: nova });
      setSuccessMsg(data?.msg || 'Senha redefinida com sucesso.');
    } catch (e) {
      setErr(e?.response?.data?.msg || 'Falha ao redefinir a senha.');
    } finally {
      setLoading(false);
    }
  }

  function closeOnlyThisWindow() {
    // tenta fechar o pop-up; se não puder, redireciona para /login
    window.close();
    // alguns navegadores não fecham janelas abertas manualmente:
    setTimeout(() => {
      try {
        if (!window.closed) {
          window.location.href = '/login';
        }
      } catch {
        window.location.href = '/login';
      }
    }, 150);
  }

  // layout: cartão central
  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: '#f5f7fb',
      padding: '24px'
    }}>
      <div className="card shadow-sm" style={{ width: '100%', maxWidth: 420 }}>
        <div className="card-body">
          {!successMsg ? (
            <>
              <h5 className="mb-3 text-center">Definir nova senha</h5>
              <form onSubmit={submit}>
                <div className="mb-2">
                  <label className="form-label">Nova senha</label>
                  <div className="input-group">
                    <input
                      type={showPwd ? 'text' : 'password'}
                      className="form-control"
                      value={nova}
                      onChange={(e) => setNova(e.target.value)}
                      autoFocus
                      required
                    />
                    <button
                      type="button"
                      className="btn btn-outline-secondary"
                      onClick={() => setShowPwd(s => !s)}
                      title={showPwd ? 'Ocultar senha' : 'Mostrar senha'}
                    >
                      {showPwd ? 'Ocultar' : 'Mostrar'}
                    </button>
                  </div>
                  {/* medidor de força */}
                  <div className="mt-2">
                    <div className="progress" role="progressbar" aria-valuenow={progressPct} aria-valuemin="0" aria-valuemax="100">
                      <div className={`progress-bar ${progressClass}`} style={{ width: `${progressPct}%` }} />
                    </div>
                    <div className="small text-muted mt-1">Força: {label}</div>
                  </div>
                </div>

                <div className="mb-3">
                  <label className="form-label">Confirmar nova senha</label>
                  <input
                    type={showPwd ? 'text' : 'password'}
                    className="form-control"
                    value={confirm}
                    onChange={(e) => setConfirm(e.target.value)}
                    required
                  />
                </div>

                {err && <div className="alert alert-danger py-2">{err}</div>}

                <button disabled={loading} className="btn btn-primary w-100" type="submit">
                  {loading ? 'Salvando...' : 'Salvar nova senha'}
                </button>
              </form>
            </>
          ) : (
            <>
              <h5 className="mb-2 text-center">Senha alterada!</h5>
              <div className="alert alert-success">{successMsg}</div>
              <button className="btn btn-outline-primary w-100" onClick={closeOnlyThisWindow}>
                Voltar para o login
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
