// frontend/src/pages/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api, { setToken, setUser } from '../api';

export default function Login(){
  const nav = useNavigate();
  const [email,setEmail] = useState('');
  const [senha,setSenha] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function submit(e){
    e.preventDefault();
    setError('');
    setLoading(true);
    try{
      const { data } = await api.post('/auth/login', { email, password: senha });
      const token = data.access_token;
      setToken(token);         // grava no localStorage
      setUser(data.user);      // grava no localStorage

      // abre o app em uma NOVA JANELA (popup real)
      const features = 'width=1200,height=720,menubar=no,toolbar=no,location=no,status=no,scrollbars=yes,resizable=yes';
      const win = window.open('/app', 'AJ7App', features);

      // fallback: se o navegador bloquear o popup, navega na mesma aba
      if(!win){
        nav('/app');
      }
    }catch(err){
      setError(err?.response?.data?.msg || 'Falha no login');
    }finally{
      setLoading(false);
    }
  }

  return (
    <div className="row justify-content-center">
      <div className="col-12 col-md-4">
        <h3 className="mb-3">Entrar</h3>
        <form onSubmit={submit}>
          <input className="form-control mb-2" placeholder="E-mail" value={email} onChange={e=>setEmail(e.target.value)} required />
          <input className="form-control mb-2" placeholder="Senha" type="password" value={senha} onChange={e=>setSenha(e.target.value)} required />
          {error && <div className="alert alert-danger py-2">{error}</div>}
          <button disabled={loading} className="btn btn-primary w-100">{loading? 'Entrando...' : 'Entrar'}</button>
        </form>
        <div className="small text-muted mt-3">
          Dica: caso a janela n√£o abra, verifique o bloqueador de pop-ups do navegador.
        </div>
      </div>
    </div>
  );
}
