import React, { useState } from 'react';
import api from '../api';

export default function ResetPassword({ email, onClose }) {
  const [senha, setSenha] = useState('');
  const [confirmar, setConfirmar] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  async function handleSubmit(e) {
    e.preventDefault();
    setError('');

    if (senha !== confirmar) {
      setError('As senhas não coincidem.');
      return;
    }

    try {
      await api.post('/auth/reset-password', { email, password: senha });
      setSuccess(true);
    } catch {
      setError('Erro ao redefinir senha.');
    }
  }

  return (
    <div className="modal-backdrop show" style={{ display: 'block' }}>
      <div className="modal" style={{ display: 'block' }}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Definir nova senha</h5>
              <button type="button" className="btn-close" onClick={onClose}></button>
            </div>
            <div className="modal-body">
              {success ? (
                <div className="alert alert-success">
                  Senha alterada com sucesso! Agora você já pode entrar.
                </div>
              ) : (
                <form onSubmit={handleSubmit}>
                  <input
                    type="password"
                    className="form-control mb-2"
                    placeholder="Nova senha"
                    value={senha}
                    onChange={(e) => setSenha(e.target.value)}
                    required
                  />
                  <input
                    type="password"
                    className="form-control mb-2"
                    placeholder="Confirmar nova senha"
                    value={confirmar}
                    onChange={(e) => setConfirmar(e.target.value)}
                    required
                  />
                  {error && <div className="alert alert-danger py-2">{error}</div>}
                  <button className="btn btn-primary w-100">Alterar senha</button>
                </form>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
