import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Sales from './pages/SalesPage'; // corrigido
import AdminCadastros from './pages/AdminCadastros';
import ManageBanks from './pages/ManageBanks';
import ManageStores from './pages/ManageStores';
import ManageSellers from './pages/ManageSellers';
import DashboardHome from './pages/DashboardHome';
import { getUserInfo, setToken } from './api';

function App() {
  const [user, setUser] = React.useState(null);

  React.useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setToken(token);
      getUserInfo()
        .then((res) => {
          setUser(res);
        })
        .catch(() => {
          localStorage.removeItem('token');
          setUser(null);
        });
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            user ? (
              <Dashboard user={user} onLogout={handleLogout} />
            ) : (
              <Login onLogin={setUser} />
            )
          }
        />
        <Route
          path="/sales"
          element={
            user ? (
              <Sales user={user} />
            ) : (
              <Navigate to="/" />
            )
          }
        />
        <Route
          path="/cadastros"
          element={
            user && user.role === 'admin' ? (
              <AdminCadastros />
            ) : (
              <Navigate to="/" />
            )
          }
        />
        <Route
          path="/cadastros/bancos"
          element={
            user && user.role === 'admin' ? (
              <ManageBanks />
            ) : (
              <Navigate to="/" />
            )
          }
        />
        <Route
          path="/cadastros/lojas"
          element={
            user && user.role === 'admin' ? (
              <ManageStores />
            ) : (
              <Navigate to="/" />
            )
          }
        />
        <Route
          path="/cadastros/vendedores"
          element={
            user && user.role === 'admin' ? (
              <ManageSellers />
            ) : (
              <Navigate to="/" />
            )
          }
        />
        <Route
          path="/dashboard"
          element={
            user ? (
              <DashboardHome user={user} />
            ) : (
              <Navigate to="/" />
            )
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
