import React, { useEffect, useState, useMemo } from 'react';
import api, { getUserInfo } from '../api';

export default function SalesList(){
  const [items,setItems] = useState([]);
  const [cliente_nome,setCliente] = useState('');
  const [cliente_documento,setDoc] = useState('');
  const [status,setStatus] = useState('');
  const [banco,setBanco] = useState('');

  const user = getUserInfo();
  const isAdmin = user?.role === 'admin';

  // vendedores para filtro (apenas admin)
  const [sellers, setSellers] = useState([]);
  const [vendedorId, setVendedorId] = useState('');

  useEffect(() => {
    async function loadSellers(){
      if(isAdmin){
        try{
          const { data } = await api.get('/sellers');
          setSellers(data);
        }catch(e){}
      }
    }
    loadSellers();
  }, [isAdmin]);

  async function fetch(){
    const params = { cliente_nome, cliente_documento, status, banco };
    if(isAdmin && vendedorId) params.vendedor_id = vendedorId;
    const { data } = await api.get('/sales', { params });
    setItems(data);
  }

  useEffect(()=>{ fetch(); /* eslint-disable-next-line */ },[]);

  // Exportar CSV
  const csvContent = useMemo(() => {
    const header = [
      'ID',
      ...(isAdmin ? ['Vendedor'] : []),
      'Cliente','Documento','Valor','Banco','Status','Comissao','Data'
    ];
    const rows = items.map(v => ([
      v.id,
      ...(isAdmin ? [v.vendedor_nome || v.vendedor_id] : []),
      v.cliente_nome,
      v.cliente_documento,
      String(Number(v.valor).toFixed(2)).replace('.', ','),
      v.banco,
      v.status,
      String(Number(v.comissao||0).toFixed(2)).replace('.', ','),
      v.data_venda ? new Date(v.data_venda).toLocaleString('pt-BR') : ''
    ]));
    const all = [header, ...rows]
      .map(cols => cols.map(c => `"${String(c).replace(/"/g,'""')}"`).join(';'))
      .join('\r\n');
    return 'data:text/csv;charset=utf-8,' + encodeURIComponent(all);
  }, [items, isAdmin]);

  function downloadCsv(){
    const a = document.createElement('a');
    a.href = csvContent;
    a.download = 'vendas.csv';
    document.body.appendChild(a);
    a.click();
    a.remove();
  }

  return (
    <div>
      <h4 className="mb-3">Vendas</h4>
      <div className="row g-2 mb-3">
        {isAdmin && (
          <div className="col-md-3">
            <select className="form-select" value={vendedorId} onChange={e=>setVendedorId(e.target.value)}>
              <option value="">Todos os vendedores</option>
              {sellers.map(s => (
                <option key={s.id} value={s.id}>{s.nome}</option>
              ))}
            </select>
          </div>
        )}
        <div className="col-md-3">
          <input className="form-control" placeholder="Cliente" value={cliente_nome} onChange={e=>setCliente(e.target.value)} />
        </div>
        <div className="col-md-3">
          <input className="form-control" placeholder="CPF/CNPJ" value={cliente_documento} onChange={e=>setDoc(e.target.value)} />
        </div>
        <div className="col-md-2">
          <input className="form-control" placeholder="Banco" value={banco} onChange={e=>setBanco(e.target.value)} />
        </div>
        <div className="col-md-1">
          <select className="form-select" value={status} onChange={e=>setStatus(e.target.value)}>
            <option value="">Status</option>
            <option value="enviada">Enviada</option>
            <option value="aceita">Aceita</option>
            <option value="recusada">Recusada</option>
          </select>
        </div>
        <div className="col-md-12 d-flex gap-2 mt-2">
          <button className="btn btn-secondary" onClick={fetch}>Buscar</button>
          <button className="btn btn-outline-primary" onClick={downloadCsv}>Exportar CSV</button>
        </div>
      </div>

      <div className="table-responsive">
        <table className="table table-striped align-middle">
          <thead className="table-dark">
            <tr>
              <th>ID</th>
              {isAdmin && <th>Vendedor</th>}
              <th>Cliente</th>
              <th>Documento</th>
              <th>Valor</th>
              <th>Banco</th>
              <th>Status</th>
              <th>Comissão</th>
              <th>Data</th>
            </tr>
          </thead>
          <tbody>
            {items.map(v => (
              <tr key={v.id}>
                <td>{v.id}</td>
                {isAdmin && <td>{v.vendedor_nome || v.vendedor_id}</td>}
                <td>{v.cliente_nome}</td>
                <td>{v.cliente_documento}</td>
                <td>{Number(v.valor).toLocaleString('pt-BR',{style:'currency',currency:'BRL'})}</td>
                <td>{v.banco}</td>
                <td>{v.status}</td>
                <td>{Number(v.comissao||0).toLocaleString('pt-BR',{style:'currency',currency:'BRL'})}</td>
                <td>{v.data_venda ? new Date(v.data_venda).toLocaleString('pt-BR') : ''}</td>
              </tr>
            ))}
            {items.length === 0 && (
              <tr>
                <td colSpan={isAdmin ? 9 : 8} className="text-center text-muted py-4">
                  Nenhuma venda encontrada.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
