import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { getUserInfo } from '../api';

export default function Shell({ children, onLogout }) {
  const user = getUserInfo();
  const nav = useNavigate();
  const { pathname } = useLocation();

  const isActive = (path) => pathname === path;

  const brandBg = '#0a214a'; // azul escuro
  const navBg   = '#0f2c66'; // barra navegação
  const textLt  = '#ffffff';

  return (
    <div className="min-vh-100 d-flex flex-column" style={{ background: '#f6f8fb' }}>
      {/* Topo */}
      <header style={{ background: brandBg, color: textLt }}>
        <div className="container py-2 d-flex align-items-center justify-content-between">
          <div className="d-flex align-items-center gap-2">
            {/* Se quiser exibir logo: coloque em src/assets/logo-aj7.png e descomente a linha abaixo */}
            {/* <img src={require('../assets/logo-aj7.png')} alt="AJ7" height={36} /> */}
            <div className="fw-bold">AJ7 • Comissões</div>
          </div>

          <div className="text-end">
            <div className="small">Logado como:</div>
            <div className="fw-semibold">{user?.nome} <span className="opacity-75">({user?.role})</span></div>
            <button
              className="btn btn-sm btn-outline-light mt-1"
              onClick={() => {
                localStorage.removeItem('token');
                localStorage.removeItem('user');
                onLogout?.();
                nav('/login', { replace: true });
              }}
            >
              Sair
            </button>
          </div>
        </div>
      </header>

      {/* Barra de navegação */}
      <nav style={{ background: navBg }}>
        <div className="container d-flex gap-3 py-2">
          <Link className={`nav-link ${isActive('/app') ? 'active text-white' : 'text-white-50'}`} to="/app">
            Dashboard
          </Link>

          <Link className={`nav-link ${isActive('/sales') ? 'active text-white' : 'text-white-50'}`} to="/sales">
            Vendas
          </Link>

          {user?.role === 'admin' && (
            <>
              <Link className={`nav-link ${isActive('/cadastros') ? 'active text-white' : 'text-white-50'}`} to="/cadastros">
                Cadastros
              </Link>
              <Link className={`nav-link ${isActive('/cadastros/bancos') ? 'active text-white' : 'text-white-50'}`} to="/cadastros/bancos">
                Bancos
              </Link>
              <Link className={`nav-link ${isActive('/cadastros/lojas') ? 'active text-white' : 'text-white-50'}`} to="/cadastros/lojas">
                Lojas
              </Link>
              <Link className={`nav-link ${isActive('/cadastros/vendedores') ? 'active text-white' : 'text-white-50'}`} to="/cadastros/vendedores">
                Vendedores
              </Link>
            </>
          )}
        </div>
      </nav>

      {/* Conteúdo */}
      <main className="container flex-grow-1 py-4">
        {children}
      </main>

      <footer className="py-3 text-center text-muted small">
        © {new Date().getFullYear()} Daniel Correa — Todos os direitos reservados.
      </footer>
    </div>
  );
}
