import React from 'react';
import { Routes, Route, Navigate, Link, useNavigate } from 'react-router-dom';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import PrivateRoute from './components/PrivateRoute';
import { setToken } from './api';

function Navbar(){
  const nav = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || 'null');
  const isAuth = !!localStorage.getItem('token');

  function logout(){
    setToken(null);
    localStorage.removeItem('user');
    nav('/login');
  }

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow-sm">
      <div className="container-fluid">
        <Link className="navbar-brand d-flex align-items-center gap-2" to="/app">
          <i className="bi bi-speedometer2"></i>
          <span>Comissões</span>
        </Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navMain">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navMain">
          <ul className="navbar-nav me-auto"></ul>
          {isAuth ? (
            <div className="d-flex align-items-center gap-3">
              <div className="text-light small">
                <i className="bi bi-person-circle me-1"></i>
                {user?.nome || 'Usuário'}{user?.role ? ` • ${user.role}` : ''}
              </div>
              <button className="btn btn-outline-light btn-sm" onClick={logout}>
                <i className="bi bi-box-arrow-right me-1"></i>
                Sair
              </button>
            </div>
          ) : (
            <Link to="/login" className="btn btn-outline-light btn-sm">Entrar</Link>
          )}
        </div>
      </div>
    </nav>
  );
}

export default function App(){
  return (
    <div className="min-vh-100 d-flex flex-column" style={{background: '#f6f7fb'}}>
      <Navbar/>
      <main className="container py-4 flex-grow-1">
        <Routes>
          <Route path="/login" element={<Login/>} />
          <Route path="/app" element={<PrivateRoute><Dashboard/></PrivateRoute>} />
          <Route path="*" element={<Navigate to="/app" replace />} />
        </Routes>
      </main>
      <footer className="bg-dark text-light py-2 small text-center">© {new Date().getFullYear()} Comissões</footer>
    </div>
  );
}
