// frontend/src/pages/SalesPage.jsx
import React, { useState } from 'react';
import AddSale from '../sections/AddSale';
import SalesList from '../sections/SalesList';
import { getUserInfo } from '../api';
import Modal from '../components/Modal';

export default function SalesPage(){
  const user = getUserInfo();
  const isAdmin = user?.role === 'admin';

  const [showModal, setShowModal] = useState(false);
  const [refreshKey, setRefreshKey] = useState(0);

  return (
    <div>
      <div className="d-flex align-items-center mb-3">
        <h3 className="m-0">Vendas</h3>
        <div className="ms-auto">
          <button className="btn btn-primary" onClick={()=>setShowModal(true)}>
            Nova venda
          </button>
        </div>
      </div>

      {/* Modal de cadastro */}
      <Modal
        open={showModal}
        onClose={()=>setShowModal(false)}
        title="Cadastrar venda"
        width={900}
        height={600}
      >
        <AddSale onCreated={() => { setRefreshKey(k => k+1); setShowModal(false); }} />
      </Modal>

      <SalesList key={refreshKey} />
    </div>
  );
}
