import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import api from '../api';

export default function ResetPassword(){
  const [params] = useSearchParams();
  const nav = useNavigate();
  const [token, setToken] = useState('');
  const [senha, setSenha] = useState('');
  const [confirm, setConfirm] = useState('');
  const [msg, setMsg] = useState('');
  const [ok, setOk] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(()=>{
    setToken(params.get('token') || '');
  },[params]);

  async function submit(e){
    e.preventDefault();
    setMsg('');
    if(!token) return setMsg('Token inválido.');
    if(senha.length < 6) return setMsg('A nova senha deve ter pelo menos 6 caracteres.');
    if(senha !== confirm) return setMsg('As senhas não conferem.');

    setLoading(true);
    try{
      const { data } = await api.post('/auth/reset', { token, nova_senha: senha });
      setOk(true);
      setMsg(data?.msg || 'Senha redefinida com sucesso.');
      // opcional: redireciona para login após 2s
      setTimeout(()=> nav('/login'), 2000);
    }catch(err){
      setMsg(err?.response?.data?.msg || 'Falha ao redefinir senha.');
    }finally{
      setLoading(false);
    }
  }

  return (
    <div className="container py-4">
      <div className="row justify-content-center">
        <div className="col-12 col-md-5">
          <h3 className="mb-3">Redefinir senha</h3>
          <form onSubmit={submit}>
            <div className="mb-2">
              <label className="form-label">Nova senha</label>
              <input
                type="password"
                className="form-control"
                value={senha}
                onChange={(e)=>setSenha(e.target.value)}
                minLength={6}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Confirmar senha</label>
              <input
                type="password"
                className="form-control"
                value={confirm}
                onChange={(e)=>setConfirm(e.target.value)}
                minLength={6}
                required
              />
            </div>
            {msg && (
              <div className={`alert ${ok ? 'alert-success' : 'alert-danger'}`}>
                {msg}
              </div>
            )}
            <button className="btn btn-primary" disabled={loading}>
              {loading ? 'Salvando...' : 'Redefinir senha'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
