import React from 'react';
import { Routes, Route, Navigate, Link, useNavigate, useLocation } from 'react-router-dom';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import PrivateRoute from './components/PrivateRoute';
import ManageBanks from './pages/ManageBanks';
import ManageStores from './pages/ManageStores';
import ManageSellers from './pages/ManageSellers';
import { setToken } from './api';
import 'bootstrap/dist/css/bootstrap.min.css';
import './theme.css';

function TopBar(){
  const nav = useNavigate();
  const isLogged = !!localStorage.getItem('token_v2');

  function logout(){
    setToken(null);
    nav('/login');
  }

  return (
    <nav className="navbar">
      <div className="container-fluid d-flex justify-content-between align-items-center">
        <div className="d-flex align-items-center gap-3">
          {/* <img src="/logo.png" alt="Logo AJ7" height="36" /> */}
          <Link to="/" className="navbar-brand fw-semibold">Comissões • AJ7</Link>
          {isLogged && (
            <div className="d-flex gap-3">
              <Link className="nav-link text-white-50" to="/app">Dashboard</Link>
              <Link className="nav-link text-white-50" to="/banks">Bancos</Link>
              <Link className="nav-link text-white-50" to="/stores">Lojas Parceiras</Link>
              <Link className="nav-link text-white-50" to="/sellers">Vendedores</Link>
            </div>
          )}
        </div>
        {isLogged && (
          <button className="btn btn-sm btn-primary" onClick={logout}>Sair</button>
        )}
      </div>
    </nav>
  );
}

export default function App(){
  const location = useLocation();
  const isLogin = location.pathname === '/login';

  return (
    <div className="d-flex flex-column min-vh-100">
      {/* Esconde a TopBar na tela de login */}
      {!isLogin && <TopBar />}

      {/* Para login, a própria página controla o layout (full screen) */}
      <div className={!isLogin ? 'container py-4 flex-grow-1' : 'flex-grow-1'}>
        <Routes>
          <Route path="/login" element={<Login/>} />
          <Route path="/app" element={<PrivateRoute><Dashboard/></PrivateRoute>} />
          <Route path="/banks" element={<PrivateRoute><ManageBanks/></PrivateRoute>} />
          <Route path="/stores" element={<PrivateRoute><ManageStores/></PrivateRoute>} />
          <Route path="/sellers" element={<PrivateRoute><ManageSellers/></PrivateRoute>} />
          <Route path="*" element={<Navigate to="/app" replace />} />
        </Routes>
      </div>

      {/* Mantém o rodapé em todas as telas (se não quiser no login, dá pra ocultar também) */}
      <footer className="bg-light text-center py-3 border-top">
        <small>© Daniel Correa</small>
      </footer>
    </div>
  );
}
