// frontend/src/pages/ResetPassword.jsx
import React, { useMemo, useState } from 'react';
import api from '../api';

function useQuery() {
  return useMemo(() => new URLSearchParams(window.location.search), []);
}

export default function ResetPassword() {
  const q = useQuery();
  const emailQS = (q.get('email') || '').trim().toLowerCase();

  const [email] = useState(emailQS);
  const [pwd, setPwd] = useState('');
  const [pwd2, setPwd2] = useState('');
  const [loading, setLoading] = useState(false);
  const [okMsg, setOkMsg] = useState('');
  const [err, setErr] = useState('');

  async function submit(e) {
    e.preventDefault();
    setErr('');
    setOkMsg('');

    if (!email) {
      setErr('E-mail n達o informado.');
      return;
    }
    if (pwd.length < 6) {
      setErr('A senha deve ter pelo menos 6 caracteres.');
      return;
    }
    if (pwd !== pwd2) {
      setErr('As senhas n達o conferem.');
      return;
    }

    setLoading(true);
    try {
      const { data } = await api.post('/auth/reset-password', { email, password: pwd });
      setOkMsg(data?.msg || 'Senha redefinida com sucesso.');
    } catch (e) {
      const msg = e?.response?.data?.msg || 'Falha ao redefinir a senha.';
      setErr(msg);
    } finally {
      setLoading(false);
    }
  }

  const container = {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: '#f6f7f9',
    padding: 16,
  };

  const card = {
    width: '100%',
    maxWidth: 420,
    background: '#fff',
    borderRadius: 10,
    boxShadow: '0 10px 30px rgba(0,0,0,0.15)',
    padding: 20,
  };

  return (
    <div style={container}>
      <div style={card}>
        <h5 className="mb-3 text-center">Definir nova senha</h5>

        <form onSubmit={submit}>
          <div className="mb-2">
            <label className="form-label">E-mail</label>
            <input className="form-control" value={email} disabled />
          </div>

          <div className="mb-2">
            <label className="form-label">Nova senha</label>
            <input
              className="form-control"
              type="password"
              value={pwd}
              onChange={e => setPwd(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Confirmar senha</label>
            <input
              className="form-control"
              type="password"
              value={pwd2}
              onChange={e => setPwd2(e.target.value)}
              required
            />
          </div>

          {err && <div className="alert alert-danger py-2">{err}</div>}
          {okMsg && <div className="alert alert-success py-2">{okMsg}</div>}

          <button className="btn btn-primary w-100" type="submit" disabled={loading}>
            {loading ? 'Salvando...' : 'Salvar nova senha'}
          </button>

          {okMsg && (
            <div className="text-center mt-3">
              <a href="/login" className="btn btn-link">Voltar ao login</a>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}
