import axios from 'axios';

const api = axios.create({ baseURL: 'http://127.0.0.1:5000' });

export function setToken(token){
  if(token){
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    localStorage.setItem('token_v2', token);
  } else {
    delete api.defaults.headers.common['Authorization'];
    localStorage.removeItem('token_v2');
  }
}

export function setUserInfo(user){
  if(user){
    localStorage.setItem('user_info_v1', JSON.stringify(user));
  }else{
    localStorage.removeItem('user_info_v1');
  }
}
export function getUserInfo(){
  try{
    return JSON.parse(localStorage.getItem('user_info_v1') || 'null');
  }catch{ return null; }
}

const saved = localStorage.getItem('token_v2');
if(saved) setToken(saved);

export default api;
