import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api, { setToken } from '../api';

export default function Login(){
  const nav = useNavigate();
  const [email,setEmail] = useState('');
  const [senha,setSenha] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function submit(e){
    e.preventDefault();
    setError('');
    setLoading(true);
    try{
      // Envia "password" e "senha" para compatibilizar backends diferentes
      const { data } = await api.post('/auth/login', { email, password: senha, senha });
      const token = data.access_token;
      const user = data.user || null; // se o backend enviar
      setToken(token);
      if(user) localStorage.setItem('user', JSON.stringify(user));
      nav('/app');
    }catch(err){
      setError(err?.response?.data?.msg || 'Falha no login');
    }finally{
      setLoading(false);
    }
  }

  return (
    <div className="d-flex align-items-center justify-content-center" style={{minHeight:'70vh'}}>
      <div className="card shadow-sm" style={{maxWidth: 420, width: '100%'}}>
        <div className="card-body p-4">
          <h4 className="mb-3 text-center"><i className="bi bi-shield-lock me-2"></i>Entrar</h4>
          <form onSubmit={submit}>
            <div className="mb-3">
              <label className="form-label">E-mail</label>
              <input className="form-control" placeholder="seu@email.com" value={email} onChange={e=>setEmail(e.target.value)} required />
            </div>
            <div className="mb-3">
              <label className="form-label">Senha</label>
              <input className="form-control" type="password" placeholder="••••••" value={senha} onChange={e=>setSenha(e.target.value)} required />
            </div>
            {error && <div className="alert alert-danger py-2 small">{error}</div>}
            <button disabled={loading} className="btn btn-primary w-100">
              {loading ? 'Entrando…' : (<><i className="bi bi-box-arrow-in-right me-2"></i>Entrar</>)}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
