// frontend/src/pages/SalesPage.jsx
import React, { useEffect, useState } from 'react';
import SalesList from '../sections/SalesList';
import { getUserInfo } from '../api';

export default function SalesPage(){
  const user = getUserInfo();
  const isAdmin = user?.role === 'admin';

  const [refreshKey, setRefreshKey] = useState(0);

  function openNewSale(){
    const features = 'width=1000,height=640,menubar=no,toolbar=no,location=no,status=no,scrollbars=yes,resizable=yes';
    window.open('/sales/new', 'AJ7NovaVenda', features);
  }

  // Atualiza lista quando a janela de cadastro sinalizar
  useEffect(() => {
    function onStorage(e){
      if(e.key === 'sales_refresh'){
        setRefreshKey(k => k + 1);
      }
    }
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
  }, []);

  return (
    <div>
      <div className="d-flex align-items-center mb-3">
        <h3 className="m-0">Vendas</h3>
        <div className="ms-auto">
          <button className="btn btn-primary" onClick={openNewSale}>
            Nova venda
          </button>
        </div>
      </div>

      <SalesList key={refreshKey} />
    </div>
  );
}
