// frontend/src/pages/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api, { setToken, setUser } from '../api';

export default function Login(){
  const nav = useNavigate();
  const [email,setEmail] = useState('');
  const [senha,setSenha] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function submit(e){
    e.preventDefault();
    setError('');
    setLoading(true);
    try{
      const { data } = await api.post('/auth/login', { email, password: senha });
      const token = data.access_token;
      setToken(token);
      setUser(data.user);
      nav('/app');
    }catch(err){
      setError(err?.response?.data?.msg || 'Falha no login');
    }finally{
      setLoading(false);
    }
  }

  return (
    <div
      className="container-fluid p-0"
      style={{ minHeight: '100vh', display: 'flex' }}
    >
      {/* Coluna esquerda: logo com fundo azul escuro */}
      <div
        className="d-none d-md-flex align-items-center justify-content-center"
        style={{
          flex: 1,
          backgroundColor: '#0B2A4A', // azul escuro
          padding: '24px'
        }}
      >
        <img
          src="/logo.png"
          alt="AJ7 Seguros"
          style={{ maxWidth: '70%', height: 'auto', filter: 'drop-shadow(0 4px 12px rgba(0,0,0,0.15))' }}
        />
      </div>

      {/* Coluna direita: formulário */}
      <div
        className="d-flex align-items-center justify-content-center"
        style={{
          flex: 1,
          padding: '24px'
        }}
      >
        <div className="w-100" style={{ maxWidth: 380 }}>
          <h3 className="mb-3">Entrar</h3>
          <form onSubmit={submit}>
            <div className="mb-2">
              <input
                className="form-control"
                placeholder="E-mail"
                type="email"
                value={email}
                onChange={e=>setEmail(e.target.value)}
                required
                autoFocus
              />
            </div>
            <div className="mb-2">
              <input
                className="form-control"
                placeholder="Senha"
                type="password"
                value={senha}
                onChange={e=>setSenha(e.target.value)}
                required
              />
            </div>

            {error && <div className="alert alert-danger py-2">{error}</div>}

            <button disabled={loading} className="btn btn-primary w-100">
              {loading ? 'Entrando...' : 'Entrar'}
            </button>
          </form>

          <div className="text-center text-muted small mt-3">
            © Daniel Correa
          </div>
        </div>
      </div>
    </div>
  );
}
