import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api, { setToken } from '../api';

export default function Login(){
  const nav = useNavigate();
  const [email,setEmail] = useState('');
  const [senha,setSenha] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function submit(e){
    e.preventDefault();
    setError('');
    setLoading(true);
    try{
      const { data } = await api.post('/auth/login', { email, password: senha });
      setToken(data.access_token);
      nav('/app');
    }catch(err){
      setError(err?.response?.data?.msg || 'Falha no login');
    }finally{
      setLoading(false);
    }
  }

  return (
    <div className="row justify-content-center">
      <div className="col-12 col-md-5">
        <div className="card shadow-sm">
          <div className="card-body p-4">
            <div className="text-center mb-3">
              {/* <img src="/logo.png" alt="Logo AJ7" height="64" /> */}
            </div>
            <h4 className="mb-3 text-center">Entrar</h4>
            <form onSubmit={submit}>
              <input className="form-control mb-2" placeholder="E-mail" value={email} onChange={e=>setEmail(e.target.value)} required />
              <input className="form-control mb-2" placeholder="Senha" type="password" value={senha} onChange={e=>setSenha(e.target.value)} required />
              {error && <div className="alert alert-danger py-2">{error}</div>}
              <button disabled={loading} className="btn btn-primary w-100">{loading? 'Entrando...' : 'Entrar'}</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
